@import url("https://cdn.jsdelivr.net/npm/tippy.js@6.3.7/dist/tippy.css");
/*  Blueprint stylesheets. To keep in this order. */
@import url("https://cdn.jsdelivr.net/npm/normalize.css@^8.0.1");
@import url("https://cdn.jsdelivr.net/npm/@blueprintjs/icons@^5.14.0/lib/css/blueprint-icons.css");
@import url("https://cdn.jsdelivr.net/npm/@blueprintjs/core@^5.14.0/lib/css/blueprint.css");
.bpmn-container {
  padding-top: 0.5rem;
  padding-bottom: 0.5rem;
  justify-content: center;
  border-radius: 0px;
  border: none;
  /* This ensures that the parts of the diagram outside the container are not displayed. */
  overflow: "hidden";
  backgroundColor: "white";
}

.zoom-btns-container {
  display: flex;
  flex-direction: row;
  padding-top: 1rem;
  padding-left: 1rem;
}

.zoom-btns-container > button:not(:last-child) {
  margin-right: 1rem;
}

.zoom-btn {
  display: flex;
  align-items: center;
}

.box {
  float: left;
  height: 15px;
  width: 15px;
  border: 1px solid black;
  clear: both;
  margin-top: 2px;
}

.red {
  background-color: red;
}

.green {
  background-color: green;
}

.blue {
  background-color: blue;
}

.black {
  background-color: black;
}

.grey {
  background-color: grey;
}

