import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';
const OVERLAY_STYLES = [{
  key: 'failed',
  popoverLabel: 'in error',
  position: 'top-right',
  color: 'red'
}, {
  key: 'executing',
  popoverLabel: 'executing',
  position: 'middle-right',
  color: 'blue'
}, {
  key: 'readyWaiting',
  popoverLabel: 'waiting',
  position: 'bottom-right',
  color: 'green'
}, {
  key: 'completed',
  popoverLabel: 'completed',
  position: 'bottom-left',
  color: 'black'
}, {
  key: 'cancelledSkipped',
  popoverLabel: 'cancelled or skipped',
  position: 'top-left',
  color: 'grey'
}];
function loadDiagram() {
  if (!bpmnVisualization || !appsmith.model.bpmnDiagram || !appsmith.model.processName) return;
  try {
    const modelFilter = appsmith.model.processName ? {
      pools: {
        name: appsmith.model.processName
      }
    } : {};
    bpmnVisualization.load(appsmith.model.bpmnDiagram, {
      fit: {
        type: bpmnvisu.FitType.Center,
        margin: 10
      },
      modelFilter
    });
  } catch (error) {
    console.error(error);
    appsmith.triggerEvent('onErrorLoading');
  }
}
function addOverlaysAndPopovers() {
  const statesCounters = appsmith.model?.flowNodeStatesCounters;
  if (!statesCounters) return;
  const allElements = bpmnVisualization.bpmnElementsRegistry.getElementsByKinds(Object.values(bpmnvisu.ShapeBpmnElementKind).filter(type => !type.endsWith('Event') && !type.endsWith('Gateway')));
  Object.entries(statesCounters).forEach(([flowNodeName, flowNodeStates]) => {
    const element = allElements.find(el => el.bpmnSemantic.name === flowNodeName);
    if (!element) return;
    applyOverlays(element.bpmnSemantic.id, flowNodeStates);
    configurePopover(element, flowNodeStates);
  });
}
function calculateFlowNodeCount(flowNodeStates, key) {
  if (key === 'readyWaiting') {
    return (flowNodeStates.ready || 0) + (flowNodeStates.waiting || 0);
  }
  if (key === 'cancelledSkipped') {
    return (flowNodeStates.cancelled || 0) + (flowNodeStates.skipped || 0);
  }
  return flowNodeStates[key] || 0;
}
function applyOverlays(flowNodeId, flowNodeStates) {
  OVERLAY_STYLES.forEach(({
    key,
    position,
    color
  }) => {
    const count = calculateFlowNodeCount(flowNodeStates, key);
    if (count) {
      bpmnVisualization.bpmnElementsRegistry.addOverlays(flowNodeId, {
        position,
        label: count.toString(),
        style: {
          font: {
            color: 'white'
          },
          fill: {
            color
          },
          stroke: {
            color
          }
        }
      });
    }
  });
}
function generatePopoverContent(flowNodeStates) {
  return OVERLAY_STYLES.filter(({
    key
  }) => flowNodeStates[key] || flowNodeStates.ready || flowNodeStates.waiting).map(({
    key,
    popoverLabel,
    color
  }) => {
    const count = calculateFlowNodeCount(flowNodeStates, key);
    return count ? `<div class="box ${color}"></div>&nbsp; ${count} ${count === 1 ? 'instance is' : 'instances are'} ${popoverLabel}<br>` : '';
  }).join('');
}
function configurePopover(bpmnElement, flowNodeStates) {
  tippy(bpmnElement.htmlElement, {
    onShow(instance) {
      const content = generatePopoverContent(flowNodeStates);
      instance.setContent(content || '');
      if (!content) instance.popper.hidden = true;
    },
    // work perfectly on hover with or without 'diagram navigation' enable
    appendTo: window.document.getElementById('bpmn-container').parentElement,
    // https://atomiks.github.io/tippyjs/v6/all-props/#sticky
    // This has a performance cost since checks are run on every animation frame. Use this only when necessary!
    // only check the "reference" rect for changes
    sticky: 'reference',
    arrow: true,
    offset: !bpmnElement.bpmnSemantic.isShape ? [0, -40] : undefined,
    // undefined offset for tippyjs default offset
    placement: 'top',
    maxWidth: 'none',
    allowHTML: true
  });
}
function fitCenter() {
  bpmnVisualization.navigation.fit({
    type: bpmnvisu.FitType.Center
  });
}
function zoomIn() {
  bpmnVisualization.navigation.zoom(bpmnvisu.ZoomType.In);
}
function zoomOut() {
  bpmnVisualization.navigation.zoom(bpmnvisu.ZoomType.Out);
}
class BpmnVisualizationCustomColors extends bpmnvisu.BpmnVisualization {
  constructor(options) {
    super(options);
    this.configureStyle();
  }
  configureStyle() {
    const styleSheet = this.graph.getStylesheet(); // mxStylesheet

    const startEventStyle = styleSheet.styles[bpmnvisu.ShapeBpmnElementKind.EVENT_START];
    startEventStyle[StyleIdentifiers.STYLE_GRADIENT_DIRECTION] = Directions.DIRECTION_WEST;
    startEventStyle[StyleIdentifiers.STYLE_GRADIENTCOLOR] = '#FBFBEE';
    startEventStyle[StyleIdentifiers.STYLE_FILLCOLOR] = '#E9ECB1';
    startEventStyle[StyleIdentifiers.STYLE_STROKECOLOR] = '#62A928';
    [bpmnvisu.ShapeBpmnElementKind.EVENT_INTERMEDIATE_CATCH, bpmnvisu.ShapeBpmnElementKind.EVENT_INTERMEDIATE_THROW].forEach(kind => {
      const intermediateEventStyle = styleSheet.styles[kind];
      intermediateEventStyle[StyleIdentifiers.STYLE_STROKECOLOR] = '#2E6DA3';
      intermediateEventStyle[StyleIdentifiers.STYLE_FILLCOLOR] = '#ffffff'; // ensure reset if the style is redefined before
    });
    const boundaryEventStyle = styleSheet.styles[bpmnvisu.ShapeBpmnElementKind.EVENT_BOUNDARY];
    boundaryEventStyle[StyleIdentifiers.STYLE_FILLCOLOR] = '#ffffff';
    boundaryEventStyle[StyleIdentifiers.STYLE_STROKECOLOR] = '#2E6DA3';
    const endEventStyle = styleSheet.styles[bpmnvisu.ShapeBpmnElementKind.EVENT_END];
    endEventStyle[StyleIdentifiers.STYLE_GRADIENT_DIRECTION] = Directions.DIRECTION_WEST;
    endEventStyle[StyleIdentifiers.STYLE_GRADIENTCOLOR] = '#FFFFFF';
    endEventStyle[StyleIdentifiers.STYLE_FILLCOLOR] = '#F9D0C6';
    endEventStyle[StyleIdentifiers.STYLE_STROKECOLOR] = '#89151A';
    bpmnvisu.ShapeUtil.taskKinds().forEach(kind => {
      const style = styleSheet.styles[kind];
      style[StyleIdentifiers.STYLE_GRADIENT_DIRECTION] = Directions.DIRECTION_WEST;
      style[StyleIdentifiers.STYLE_GRADIENTCOLOR] = '#FBFBEE';
      style[StyleIdentifiers.STYLE_FILLCOLOR] = '#E5E6F1';
      style[StyleIdentifiers.STYLE_STROKECOLOR] = '#2C6DA3';
    });
    bpmnvisu.ShapeUtil.gatewayKinds().forEach(kind => {
      const style = styleSheet.styles[kind];
      style[StyleIdentifiers.STYLE_GRADIENT_DIRECTION] = Directions.DIRECTION_WEST;
      style[StyleIdentifiers.STYLE_GRADIENTCOLOR] = '#FBFBEE';
      style[StyleIdentifiers.STYLE_FILLCOLOR] = '#E9ECB1';
      style[StyleIdentifiers.STYLE_STROKECOLOR] = '#96A826';
    });
  }
}
class StyleIdentifiers {
  static STYLE_FILLCOLOR = 'fillColor';
  static STYLE_STROKECOLOR = 'strokeColor';
  static STYLE_GRADIENT_DIRECTION = 'gradientDirection';
  static STYLE_GRADIENTCOLOR = 'gradientColor';
  static STYLE_FONTCOLOR = 'fontColor';
  static STYLE_FONTFAMILY = 'fontFamily';
  static STYLE_FONTSIZE = 'fontSize';
  static STYLE_FONTSTYLE = 'fontStyle';
  static STYLE_SWIMLANE_FILLCOLOR = 'swimlaneFillColor';
}
class Directions {
  static DIRECTION_EAST = 'east';
  static DIRECTION_SOUTH = 'south';
  static DIRECTION_WEST = 'west';
}
class FontStyle {
  static FONT_BOLD = 1;
  static FONT_ITALIC = 2;
}
const bpmnVisualization = new BpmnVisualizationCustomColors({
  container: "bpmn-container",
  navigation: {
    enabled: true
  }
});
appsmith.onReady(() => {
  document.getElementById("zoom-in").addEventListener("click", zoomIn);
  document.getElementById("zoom-out").addEventListener("click", zoomOut);
  document.getElementById("fit-center").addEventListener("click", fitCenter);
  loadDiagram();
  addOverlaysAndPopovers();
});
appsmith.onModelChange(newModel => {
  loadDiagram();
  addOverlaysAndPopovers();
});

