import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';


const OVERLAY_STYLES = [
	{ key: 'failed', popoverLabel: 'in error', position: 'top-right', color: 'red' },
	{ key: 'executing', popoverLabel: 'executing', position: 'middle-right', color: 'blue' },
	{ key: 'readyWaiting', popoverLabel: 'waiting', position: 'bottom-right', color: 'green' },
	{ key: 'completed', popoverLabel: 'completed', position: 'bottom-left', color: 'black' },
	{ key: 'cancelledSkipped', popoverLabel: 'cancelled or skipped', position: 'top-left', color: 'grey' },
];

function loadDiagram() {
	if (!bpmnVisualization || !appsmith.model.bpmnDiagram || !appsmith.model.processName) return;

	try {
		const modelFilter = appsmith.model.processName
		? { pools: { name: appsmith.model.processName } }
		: {};

		bpmnVisualization.load(appsmith.model.bpmnDiagram, {
			fit: { type: bpmnvisu.FitType.Center, margin: 10 },
			modelFilter,
		});
	} catch (error) {
		console.error(error);
		appsmith.triggerEvent('onErrorLoading');
	}
}

function addOverlaysAndPopovers() {
	const statesCounters = appsmith.model?.flowNodeStatesCounters;
	if (!statesCounters) return;

	const allElements = bpmnVisualization.bpmnElementsRegistry.getElementsByKinds(
		Object.values(bpmnvisu.ShapeBpmnElementKind).filter(
			(type) => !type.endsWith('Event') && !type.endsWith('Gateway')
		)
	);

	Object.entries(statesCounters).forEach(([flowNodeName, flowNodeStates]) => {
		const element = allElements.find((el) => el.bpmnSemantic.name === flowNodeName);
		if (!element) return;

		applyOverlays(element.bpmnSemantic.id, flowNodeStates);
		configurePopover(element, flowNodeStates);
	});
}

function calculateFlowNodeCount(flowNodeStates, key) {
	if (key === 'readyWaiting') {
		return (flowNodeStates.ready || 0) + (flowNodeStates.waiting || 0);
	}
	if (key === 'cancelledSkipped') {
		return (flowNodeStates.cancelled || 0) + (flowNodeStates.skipped || 0);
	}
	return flowNodeStates[key] || 0;
}

function applyOverlays(flowNodeId, flowNodeStates) {
	OVERLAY_STYLES.forEach(({ key, position, color }) => {
		const count = calculateFlowNodeCount(flowNodeStates, key);

		if (count) {
			bpmnVisualization.bpmnElementsRegistry.addOverlays(flowNodeId, {
				position,
				label: count.toString(),
