export default {
	defaultAvatarBase64: "data:image/jpeg;base64,",

	forgeAvatarURL (iconId){
		let avatar = ""
		if (iconId == "icons/default/icon_user.png" || iconId == null){
			avatar = this.defaultAvatarBase64;
		}else{
			avatar = appsmith.URL.protocol + "//" + appsmith.URL.hostname + "/bonita/API/avatars/" + iconId;
		}
		return avatar
	},

	async onPageLoad () {
		await getInvoice.run();
		getCaseActivity.run();
		getComments.run();
		getDocuments.run();
		getHistory.run();
	},
	async updateDocuments() {
		getDocuments.run();
	},
	async updateTasks() {
		getCaseActivity.run();
		getHistory.run();
		getComments.run();		
	},
	async updateComments() {
		getCaseActivity.run();
		getComments.run();
	},
	getFilename () {
		Upload_Document_Filename.setValue(JSON.parse(uploadFile.data).filename);
	},
	color: '',
	getTaskColor(bpmState) {
		if (bpmState === "Ready"){
			this.color = '#14532d';
			return this.color;
		} else if(bpmState === "Completed"){
			this.color = '#1e3a8a';
			return this.color;
		}
		else {
			this.color = '#71717a';
			return this.color;
		}
	},
	convertUTCTime(utcSeconds){
		let newDate = new Date(0);
		newDate.setMilliseconds(utcSeconds);
		return newDate.toUTCString();
	}
}

