class InterventionListWidget {
  constructor() {
    this.interventionList = document.querySelector('.intervention-list');
    this.template = document.getElementById('intervention-template');
    this.interventions = [];
    this.selectedInterventionId = null;
  }
  renderInterventions() {
    if (!this.interventionList) return;

    // Rcuprer les donnes depuis appsmith.model
    if (window.appsmith && appsmith.model && appsmith.model.interventions) {
      this.interventions = appsmith.model.interventions;
    } else {
      this.interventions = [];
    }

    // Mettre  jour la barre de statuts
    this.updateStatusBar();

    // Vider le conteneur
    this.interventionList.innerHTML = '';
    if (!this.interventions || this.interventions.length === 0) {
      // Ne rien afficher si aucune intervention
      return;
    }

    // Trier les interventions par date de cration (plus rcentes en premier)
    const sortedInterventions = [...this.interventions].sort((a, b) => new Date(b.creationDate) - new Date(a.creationDate));

    // Rendre chaque intervention
    sortedInterventions.forEach(intervention => {
      const interventionElement = this.createInterventionElement(intervention);
      this.interventionList.appendChild(interventionElement);
    });
  }
  createInterventionElement(intervention) {
    if (!this.template) return document.createElement('div');
    const clone = this.template.content.cloneNode(true);
    const interventionElement = clone.querySelector('.intervention-item');

    // Remplir les donnes
    this.fillInterventionData(interventionElement, intervention);

    // Ajouter les vnements
    //this.addInterventionEvents(interventionElement, intervention);

    return interventionElement;
  }
  fillInterventionData(element, intervention) {
    // Mise  jour de la barre de statut
    const statusItems = element.querySelectorAll('.status-item');
    const currentStatus = intervention.statut;
    statusItems.forEach(item => {
      const itemStatus = item.dataset.status;
      item.classList.remove('active');
      if (itemStatus === currentStatus) {
        item.classList.add('active');
      }
    });

    // Date planifie
    const plannedDateElement = element.querySelector('.intervention-planned-date');
    plannedDateElement.textContent = `Planned date: ${this.formatDate(intervention.plannedDate)}`;

    // Date complmentaire  droite
    const dateRightElement = element.querySelector('.intervention-date-right');
    if (currentStatus === 'Canceled' && intervention.endDate) {
      dateRightElement.textContent = `Cancellation Date: ${this.formatDateTime(intervention.endDate)}`;
    } else if (currentStatus !== 'Planified' && intervention.interventionDate) {
      dateRightElement.textContent = `Intervention Date: ${this.formatDateTime(intervention.interventionDate)}`;
    } else {
      dateRightElement.textContent = '';
    }

    // Planificateur
    const plannerElement = element.querySelector('.intervention-planner');
    plannerElement.textContent = intervention.plannerName;

    // Technicien
    const technicianElement = element.querySelector('.intervention-technician');
    technicianElement.textContent = intervention.technicianName;

    // Note du technicien (rapport)
    const technicianNoteElement = element.querySelector('.technician-note');
    if (intervention.report) {
      const cleanReport = intervention.report.replace(/<[^>]*>/g, '');
      if (currentStatus === 'Canceled') {
        technicianNoteElement.textContent = cleanReport;
        technicianNoteElement.classList.add('canceled-report');
      } else {
        technicianNoteElement.textContent = cleanReport;
        technicianNoteElement.classList.remove('canceled-report');
      }
    } else {
      technicianNoteElement.textContent = '';
    }

    // Note du manager
    const managerNoteElement = element.querySelector('.manager-note');
    if (intervention.managerComment) {
      const cleanManagerComment = intervention.managerComment.replace(/<[^>]*>/g, '');
      managerNoteElement.textContent = cleanManagerComment;
    } else {
      managerNoteElement.textContent = '';
    }

    // Ajouter l'ID de l'intervention  l'lment
    element.dataset.interventionId = intervention.persistenceId;
  }

  /*addInterventionEvents(element, intervention) {
         element.addEventListener('click', (e) => {
             // Ne pas dclencher la slection si on clique sur un bouton d'action
             if (e.target.classList.contains('action-btn')) {
                 return;
             }
              this.selectIntervention(intervention.persistenceId);
              // mettre un vnement pour Appsmith
             if (window.appsmith && window.appsmith.triggerEvent) {
                 window.appsmith.triggerEvent('onInterventionSelect', {
                     interventionId: intervention.persistenceId,
                     intervention: intervention
                 });
             }
         });
     }*/

  handleModifyTask(intervention) {
    console.log('Modify task clicked for intervention:', intervention.persistenceId);

    // mettre un vnement pour Appsmith
    if (window.appsmith && window.appsmith.triggerEvent) {
      window.appsmith.triggerEvent('onModifyTask', {
        interventionId: intervention.persistenceId,
        intervention: intervention
      });
    }
  }
  handleCancelIntervention(intervention) {
    console.log('Cancel intervention clicked for intervention:', intervention.persistenceId);

    // mettre un vnement pour Appsmith
    if (window.appsmith && window.appsmith.triggerEvent) {
      window.appsmith.triggerEvent('onCancelIntervention', {
        interventionId: intervention.persistenceId,
        intervention: intervention
      });
    }
  }

  /*selectIntervention(interventionId) {
         // Retirer la slection prcdente
         document.querySelectorAll('.intervention-item').forEach(item => {
             item.classList.remove('selected-intervention');
         });
          // Slectionner la nouvelle intervention
         const selectedElement = document.querySelector(`[data-intervention-id="${interventionId}"]`);
         if (selectedElement) {
             selectedElement.classList.add('selected-intervention');
             this.selectedInterventionId = interventionId;
         }
     }*/

  getStatusClass(statut) {
    const statusMap = {
      'Planified': 'Planified',
      'Done': 'Review',
      'Postponed': 'Postponed',
      'Disapproved': 'Disapproved',
      'Canceled': 'Canceled'
    };
    return statusMap[statut] || 'Planified';
  }
  getDisplayStatus(statut) {
    const displayMap = {
      'Planified': 'Planified',
      'Done': 'Under Review',
      'Postponed': 'Postponed',
      'Disapproved': 'Disapproved',
      'Canceled': 'Canceled'
    };
    return displayMap[statut] || statut;
  }
  canShowActions(statut) {
    return statut === 'Planified';
  }
  formatDate(dateString) {
    if (!dateString) return 'Date not defined';
    const date = new Date(dateString);
    return date.toDateString(); // Format: "Tue Jun 17 2025"
  }
  formatDateTime(dateTimeString) {
    if (!dateTimeString) return 'Date not defined';
    const date = new Date(dateTimeString);
    const dateStr = date.toDateString(); // "Tue Jun 17 2025"
    const timeStr = date.toTimeString().substring(0, 5); // "15:49"
    return `${dateStr} ${timeStr}`; // "Tue Jun 17 2025 15:49"
  }

  // Mthodes publiques pour Appsmith
  updateData() {
    this.renderInterventions();
  }
  getSelectedIntervention() {
    if (this.selectedInterventionId) {
      return this.interventions.find(i => i.persistenceId === this.selectedInterventionId);
    }
    return null;
  }
  selectInterventionById(interventionId) {
    this.selectIntervention(interventionId);
  }
  updateStatusBar() {
    const statusBar = document.querySelector('.intervention-status-bar');
    if (!statusBar) return;

    // Compter les interventions par statut
    const statusCounts = {};
    this.interventions.forEach(intervention => {
      const status = intervention.statut;
      statusCounts[status] = (statusCounts[status] || 0) + 1;
    });

    // Mettre  jour chaque lment de statut
    const statusItems = statusBar.querySelectorAll('.status-item');
    statusItems.forEach(item => {
      const status = item.dataset.status;
      const count = statusCounts[status] || 0;
      const label = item.querySelector('.status-label');

      // Mettre  jour le texte avec le compte
      const displayText = item.querySelector('.status-label').textContent.split(' (')[0];
      label.textContent = count > 0 ? `${displayText} (${count})` : displayText;

      // Activer/dsactiver selon s'il y a des interventions
      if (count > 0) {
        item.classList.add('active');
      } else {
        item.classList.remove('active');
      }
    });
  }
}

// Instance globale du widget
let widgetInstance;

// Fonction de rendu pour Appsmith
function renderInterventions() {
  if (!widgetInstance) {
    widgetInstance = new InterventionListWidget();
  }
  widgetInstance.renderInterventions();
}

// Configuration Appsmith
if (window.appsmith) {
  appsmith.onReady(renderInterventions);
  appsmith.onModelChange(renderInterventions);
}

// Exposer les mthodes pour Appsmith
window.InterventionListWidget = {
  getInstance: () => widgetInstance,
  updateData: () => widgetInstance?.updateData(),
  getSelectedIntervention: () => widgetInstance?.getSelectedIntervention(),
  selectIntervention: id => widgetInstance?.selectInterventionById(id)
};

