		const statusMap = {
			'Planified': 'Planified',
			'Done': 'Review',
			'Postponed': 'Postponed',
			'Disapproved': 'Disapproved',
			'Canceled': 'Canceled'
		};
		return statusMap[statut] || 'Planified';
	}

	getDisplayStatus(statut) {
		const displayMap = {
			'Planified': 'Planified',
			'Done': 'Under Review',
			'Postponed': 'Postponed',
			'Disapproved': 'Disapproved',
			'Canceled': 'Canceled'
		};
		return displayMap[statut] || statut;
