import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm'
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm'

function App() {
	const imageList = appsmith.model.photos_ref;
	console.log(appsmith.model)
	const [modalVisible, setModalVisible] = React.useState(false);
	const [selectedImage, setSelectedImage] = React.useState(null);

	function openModal(src) {
		appsmith.triggerEvent("openModalPhoto", {url: "http://localhost:8080/bonita/API/" + src.url})
	}

	return (
		<div className="thumbnail-gallery">
		{imageList.map((src, index) => (
		<img
		key={index}
		src={"http://localhost:8080/bonita/API/" + src.url}
		onClick={() => openModal(src)}
		/>
))}
	</div>
);
}



appsmith.onReady(() => {
	/*
	 * This handler function will get called when parent application is ready.
	 * Initialize your component here
	 * more info - https://docs.appsmith.com/reference/widgets/custom#onready
	 */
	reactDom.render(<App />, document.getElementById("root"));
});

