srcDoc.js:
class TicketListWidget {
  constructor() {
    this.ticketList = document.querySelector('.ticket-list');
    this.template = document.getElementById('ticket-template');
  }

  // Mthode pour formater la date
  formatDate(dateString) {
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('en-EN', {
      dateStyle: 'medium',
      timeStyle: 'short'
    }).format(date);
  }

  // Mthode pour crer un lment de ticket
  createTicketElement(ticket) {
    const ticketElement = this.template.content.cloneNode(true);

    // Ajoute un attribut data-persistence-id sur le ticket-item
    const ticketItem = ticketElement.querySelector('.ticket-item');
    if (ticketItem) {
      ticketItem.setAttribute('data-persistence-id', ticket.persistenceId);
      ticketItem.onclick = () => onSelect(ticket.persistenceId);
    }

    // Numro de ticket avec couleur selon le statut
    const ticketNumber = ticketElement.querySelector('.ticket-number');
    ticketNumber.textContent = `${ticket.persistenceId_string}`;
    ticketNumber.classList.add(ticket.statut.replace("Under Review", "Review"));

    // Sujet du ticket
    ticketElement.querySelector('.ticket-subject').textContent = ticket.ticketSubject;
    ticketElement.querySelector('.ticket-location').textContent = ticket.location;
    ticketElement.querySelector('.ticket-location').style.fontWeight = 'bold';
    ticketElement.querySelector('.ticket-equipment').textContent = ticket.equipmentType;
    return ticketElement;
  }

  // Mthode pour mettre  jour la liste des tickets
  updateTickets(tickets) {
    // Vider la liste actuelle
    this.ticketList.innerHTML = '';

    // Ajouter chaque ticket
    tickets.forEach(ticket => {
      const ticketElement = this.createTicketElement(ticket);
      this.ticketList.appendChild(ticketElement);
    });

    // Ajustement dynamique de la largeur du container
    const container = document.querySelector('.ticket-list-container');
    if (tickets.length > 7) {
      container.style.width = '99%';
    } else {
      container.style.width = '100%';
    }

    // Scroll automatique sur le ticket slectionn
    const selectedId = appsmith.model.currentTicket;
    if (selectedId) {
      const selectedTicket = this.ticketList.querySelector(`.ticket-item[data-persistence-id='${selectedId}']`);
      if (selectedTicket) {
        selectedTicket.classList.add('selected-ticket');
        selectedTicket.scrollIntoView({
          behavior: 'smooth',
          block: 'center'
        });
      }
    }
  }
}
function renderTickets() {
  const widget = new TicketListWidget();
  if (appsmith && appsmith.model && Array.isArray(appsmith.model.filteredTickets)) {
    widget.updateTickets(appsmith.model.filteredTickets);
  }
}
function onSelect(persistenceId) {
  if (window.appsmith && typeof window.appsmith.triggerEvent === 'function') {
    window.appsmith.triggerEvent('onSelect', {
      ticketId: persistenceId
    });
  }
}
appsmith.onReady(renderTickets);
appsmith.onModelChange(renderTickets);

