class PlanningWidget {
  constructor() {
    this.planningBody = document.getElementById('planning-body');
    this.tooltip = document.getElementById('ticket-tooltip');
    this.currentData = {
      interventions: [],
      dates: [],
      technicians: []
    };
    this.selectedInterventionId = null;
    this.currentWeekStart = null;
    this.initNavigation();
  }
  initNavigation() {
    const prevButton = document.getElementById('prev-week');
    const nextButton = document.getElementById('next-week');
    if (prevButton) {
      prevButton.addEventListener('click', () => {
        this.changeWeek(-1);
      });
    }
    if (nextButton) {
      nextButton.addEventListener('click', () => {
        this.changeWeek(1);
      });
    }
  }
  changeWeek(direction) {
    if (!this.currentWeekStart) {
      // If no current week, use the first date from data
      if (this.currentData.dates && this.currentData.dates.length > 0) {
        this.currentWeekStart = new Date(this.currentData.dates[0]);
      } else {
        this.currentWeekStart = this.getWeekStartDate(new Date());
      }
    }

    // Calculate new week start
    const newWeekStart = new Date(this.currentWeekStart);
    newWeekStart.setDate(this.currentWeekStart.getDate() + direction * 7);
    this.currentWeekStart = newWeekStart;

    // Format the date as YYYY-MM-DD
    const formattedDate = this.formatDateForAppsmith(newWeekStart);

    // Trigger event for Appsmith
    if (window.appsmith && window.appsmith.triggerEvent) {
      window.appsmith.triggerEvent('onWeekChange', {
        weekStart: formattedDate
      });
    }
  }
  getWeekStartDate(date) {
    const dayOfWeek = date.getDay();
    const mondayOffset = dayOfWeek === 0 ? -6 : 1 - dayOfWeek; // Adjust for Monday start

    const monday = new Date(date);
    monday.setDate(date.getDate() + mondayOffset);
    return monday;
  }
  formatDateForAppsmith(date) {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  }
  renderPlanning() {
    if (!this.planningBody) return;

    // Rcuprer les donnes depuis appsmith.model
    if (window.appsmith && appsmith.model && appsmith.model.data) {
      this.currentData = appsmith.model.data;
    } else {
      this.currentData = {
        interventions: [],
        dates: [],
        technicians: []
      };
    }

    // Vider le conteneur
    this.planningBody.innerHTML = '';

    // Get technicians from the data structure (always show all technicians)
    const technicians = this.currentData.technicians || [];

    // Get dates from the data structure
    const weekDates = this.currentData.dates || [];

    // Get interventions from the data structure
    const interventions = this.currentData.interventions || [];
    let planningHTML = '';
    technicians.forEach(technician => {
      planningHTML += '<div class="planning-row">';

      // Technician name cell
      planningHTML += `<div class="technician-cell">${technician.name}</div>`;

      // Day cells
      weekDates.forEach((dateString, dayIndex) => {
        // Find interventions for this technician and day
        const dayInterventions = interventions.filter(item => {
          return item.technicianId === technician.id && item.date === dateString;
        });
        planningHTML += `
                    <div class="day-cell">
                        <div class="day-cell-content">
                        ${dayInterventions.map(intervention => `
                            <div class="ticket-badge ${this.getStatusClass(intervention.interventionStatut)}"
                                 data-intervention-id="${intervention.interventionId}"
                                 title="${intervention.ticketSubject}">#${intervention.ticketNumber}</div>
                        `).join('')}
                        </div>
                    </div>
                `;
      });
      planningHTML += '</div>';
    });
    this.planningBody.innerHTML = planningHTML;

    // Update header with dates
    this.updateHeaderWithDates(weekDates);

    // Add event listeners to ticket badges
    this.addTicketEventListeners();
  }
  getWeekDates() {
    // Get the first date from the data to determine the week
    if (this.currentData.length === 0) {
      return [];
    }
    const firstDate = new Date(this.currentData[0].date);
    const dayOfWeek = firstDate.getDay();
    const mondayOffset = dayOfWeek === 0 ? -6 : 1 - dayOfWeek; // Adjust for Monday start

    const monday = new Date(firstDate);
    monday.setDate(firstDate.getDate() + mondayOffset);
    const weekDates = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(monday);
      date.setDate(monday.getDate() + i);
      weekDates.push(date);
    }
    return weekDates;
  }
  updateHeaderWithDates(weekDates) {
    const headerCells = document.querySelectorAll('.header-cell:not(.technician-header)');
    headerCells.forEach((cell, index) => {
      if (weekDates[index]) {
        const date = new Date(weekDates[index]);
        const dayNames = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
        const dayName = dayNames[index];
        const dateStr = this.formatDate(date);
        cell.innerHTML = `
                    <div class="day-name">${dayName}</div>
                    <div class="day-date">${dateStr}</div>
                `;
      }
    });
  }
  addTicketEventListeners() {
    const ticketBadges = document.querySelectorAll('.ticket-badge');
    ticketBadges.forEach(badge => {
      badge.addEventListener('mouseenter', e => {
        this.showTicketTooltip(e.target);
      });
      badge.addEventListener('mouseleave', () => {
        this.hideTicketTooltip();
      });
      badge.addEventListener('click', e => {
        const interventionId = parseInt(e.target.dataset.interventionId);
        this.selectIntervention(interventionId);
      });
    });
  }
  showTicketTooltip(badgeElement) {
    const interventionId = parseInt(badgeElement.dataset.interventionId);
    const intervention = this.currentData.interventions.find(item => item.interventionId === interventionId);
    if (!intervention || !this.tooltip) return;
    this.tooltip.innerHTML = `
            <div class="tooltip-header">
                <strong>Ticket #${intervention.ticketNumber}</strong>
                <span class="tooltip-status ${this.getStatusClass(intervention.interventionStatut)}">
                    ${this.getStatusLabel(intervention.interventionStatut)}
                </span>
            </div>
            <div class="tooltip-content">
                <div class="tooltip-row subject-row">
                    <strong>Subject:</strong>
                    <span>${intervention.ticketSubject}</span>
                </div>
                <div class="tooltip-row">
                    <strong>Location:</strong> ${intervention.ticketLocation}
                </div>
                <div class="tooltip-row equipment-row">
                    <strong>Equipment:</strong>
                    <span>${intervention.ticketEquipment}</span>
                </div>
                <div class="tooltip-row">
                    <strong>Created:</strong> ${this.formatDate(intervention.ticketCreation)}
                </div>
            </div>
        `;

    // Position tooltip
    const rect = badgeElement.getBoundingClientRect();
    this.tooltip.style.left = rect.left + 'px';
    this.tooltip.style.top = rect.bottom + 5 + 'px';
    this.tooltip.style.display = 'block';
  }
  hideTicketTooltip() {
    if (this.tooltip) {
      this.tooltip.style.display = 'none';
    }
  }
  selectIntervention(interventionId) {
    const intervention = this.currentData.interventions.find(item => item.interventionId === interventionId);
    if (!intervention) return;
    this.selectedInterventionId = interventionId;

    // mettre un vnement pour Appsmith
    if (window.appsmith && window.appsmith.triggerEvent) {
      window.appsmith.triggerEvent('onInterventionSelect', {
        interventionId: interventionId,
        intervention: intervention
      });
    }
  }
  getStatusClass(status) {
    switch (status) {
      case 'Planified':
        return 'status-planned';
      case 'Done':
        return 'status-completed';
      case 'Postponed':
        return 'status-postponed';
      case 'Disapproved':
        return 'status-disapproved';
      case 'Canceled':
        return 'status-canceled';
      default:
        return 'status-default';
    }
  }
  getStatusLabel(status) {
    switch (status) {
      case 'Planified':
        return 'Planned';
      case 'Done':
        return 'Done';
      case 'Postponed':
        return 'Postponed';
      case 'Disapproved':
        return 'Disapproved';
      case 'Canceled':
        return 'Canceled';
      default:
        return status;
    }
  }
  formatDate(dateString) {
    if (!dateString) return 'Date not defined';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US');
  }

  // Mthodes publiques pour Appsmith
  updateData() {
    this.renderPlanning();
  }
  getSelectedIntervention() {
    if (this.selectedInterventionId) {
      return this.currentData.interventions.find(i => i.interventionId === this.selectedInterventionId);
    }
    return null;
  }
  selectInterventionById(interventionId) {
    this.selectIntervention(interventionId);
  }
}

// Instance globale du widget
let widgetInstance;

// Fonction de rendu pour Appsmith
function renderPlanning() {
  if (!widgetInstance) {
    widgetInstance = new PlanningWidget();
  }
  widgetInstance.renderPlanning();
}

// Configuration Appsmith
if (window.appsmith) {
  appsmith.onReady(renderPlanning);
  appsmith.onModelChange(renderPlanning);
}

// Exposer les mthodes pour Appsmith
window.PlanningWidget = {
  getInstance: () => widgetInstance,
  updateData: () => widgetInstance?.updateData(),
  getSelectedIntervention: () => widgetInstance?.getSelectedIntervention(),
  selectIntervention: id => widgetInstance?.selectInterventionById(id)
};

