  }
  .planning-row {
    grid-template-columns: 100px repeat(7, 1fr);
  }
  .technician-cell {
    padding: 8px 8px;
    font-size: 11px;
  }
  .header-cell {
    padding: 6px 3px;
    font-size: 11px;
  }
  .day-name {
    font-size: 11px;
  }
  .day-date {
    font-size: 9px;
  }
  .day-cell {
    padding: 3px;
    min-height: 40px;
  }
  .ticket-badge {
    font-size: 9px;
    padding: 3px 5px;
