@import url("https://fonts.googleapis.com/css?family=Roboto:400,500,700&display=swap");
html, body {
  overflow: hidden !important;
  height: 100% !important;
  max-height: 100% !important;
  margin: 0;
  padding: 0;
}

body, .ticket-list-container {
  font-family: "Roboto", Arial, sans-serif;
  width: 100%;
}

.ticket-list-container {
  padding: 0px;
  background-color: #fff;
  border-radius: 0px;
  border: 0;
  overflow: hidden !important;
  height: 100%;
  max-height: 100%;
}

.ticket-list-header {
  margin-bottom: 20px;
}

.ticket-list-header h2 {
  color: #333;
  margin: 0;
}

.ticket-list {
  display: flex;
  flex-direction: column;
  gap: 15px;
  overflow: hidden !important;
  height: 100%;
  max-height: 100%;
}

.ticket-item {
  background-color: #F8FAFC;
  border-radius: 6px;
  border: 1px solid #e0dede;
  padding: 10px;
  box-shadow: 0 0px 4px rgba(0, 0, 0, 0.1);
  cursor: pointer;
}

.ticket-item.selected-ticket {
  background-color: #c4d4fd;
}

.ticket-header {
  display: flex;
  align-items: flex-start;
  gap: 0px;
}

.ticket-number-col {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  min-width: 48px;
  padding-right: 10px;
}

.ticket-main-col {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  flex: 1;
  gap: 2px;
}

.ticket-number {
  display: flex;
  align-items: center; /* aligne verticalement */
  justify-content: center;
  border-radius: 0px;
  font-weight: 700;
  font-size: 16px;
  color: white;
  width: 49px;
  height: 32px;
}

.ticket-number.New {
  background-color: #be185d;
}

.ticket-number.Pending {
  background-color: #a16207;
}

.ticket-number.Assigned {
  background-color: #1e3a8a;
}

.ticket-number.Review {
  background-color: #15803d;
}

.ticket-subject {
  margin: 0;
  font-size: 14px;
  color: #2c3e50;
  font-weight: 100;
  line-height: 1.2;
  height: 52px;
  max-height: 52px;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  white-space: normal;
}

.ticket-priority {
  padding: 4px 8px;
  border-radius: 4px;
  font-size: 0.9em;
  font-weight: bold;
}

.ticket-priority.High {
  background-color: #ffebee;
  color: #c62828;
}

.ticket-priority.Medium {
  background-color: #fff3e0;
  color: #ef6c00;
}

.ticket-priority.Low {
  background-color: #e8f5e9;
  color: #2e7d32;
}

.ticket-details {
  display: flex;
  justify-content: space-between;
  margin-bottom: 10px;
  font-size: 0.9em;
  color: #666;
}

.ticket-info {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 2px;
  margin-left: 0;
}

.ticket-description {
  font-size: 0.9em;
  color: #666;
  line-height: 1.4;
}

.ticket-status {
  display: inline-block;
  padding: 2px 6px;
  border-radius: 3px;
  font-size: 0.8em;
}

.ticket-status.Closed {
  background-color: #e8f5e9;
  color: #2e7d32;
}

.ticket-status.Open {
  background-color: #e3f2fd;
  color: #1565c0;
}

.ticket-location, .ticket-equipment {
  font-size: 11.2px;
  text-align: left;
  margin-left: 0;
}

.ticket-list-container::-webkit-scrollbar,
.ticket-list::-webkit-scrollbar {
  display: none !important;
}

