			const cleanManagerComment = intervention.managerComment.replace(/<[^>]*>/g, '');
			managerNoteElement.textContent = cleanManagerComment;
		} else {
			managerNoteElement.textContent = '';
		}

		// Gestion des actions
		const modifyBtn = element.querySelector('.modify-btn');
		const cancelBtn = element.querySelector('.cancel-btn');

		if (this.canShowActions(intervention.statut)) {
			modifyBtn.style.display = 'inline-block';
			cancelBtn.style.display = 'inline-block';

			// Trouver l'index de l'intervention dans le modle
			const interventionIndex = this.interventions.findIndex(i => i.persistenceId === intervention.persistenceId);

			// Ajouter les vnements onclick
			modifyBtn.onclick = () => onModify(interventionIndex);
			cancelBtn.onclick = () => onCancel(interventionIndex);
		} else {
			modifyBtn.style.display = 'none';
			cancelBtn.style.display = 'none';
		}

		// Ajouter l'ID de l'intervention  l'lment
		element.dataset.interventionId = intervention.persistenceId;
