async function () {
  const files = photosDocumentInput.files;
  const results = [];
  for (const file of files) {
    try {
      const response = await photoUpload.run({
        file: file
      });
      const parsed = JSON.parse(response.data?.body ?? response);
      results.push(parsed);
    } catch (error) {
      console.error("Erreur upload du fichier :", file.name, error);
      showAlert("Erreur de chargement du fichier : " + file.name);
    }
  }
  return results;
}

