        statut: statut
      });
      const count = Array.isArray(response) ? response[0] : response.data?.[0] ?? 0;
      JSGeneral.ticketCounts[statut] = count;
    } catch (error) {
      showAlert("Error loading counts: " + error.message, "error");
    }
  }
}

