actionConfiguration.body:
function () {
  const interventions = JSTask.interventions || [];
  return interventions.map(intervention => {
    const tech = JSTask.technicians.find(t => t.id == intervention.technicianId);
    const plan = JSTask.planners.find(p => p.id == intervention.plannerId);
    return {
      ...intervention,
      technicianName: tech ? tech.name : "Not assigned",
      plannerName: plan ? plan.name : "Unknow"
    };
  });
}

