function () {
  JSTask.ticket = JSTickets.tickets.find(it => it.persistenceId === JSTickets.currentTicket);
  if (!JSTask.ticket) {
    JSTickets.unselectTicket();
    return;
  }
  JSTask.task = JSTask.ticket.task;
  JSTask.interventions = [];
  JSTask.context = {};
  JSTask.initiator = {};
  JSTask.photo = "";
  JSTask.payload = {};
  if (JSTask.task) {
    JSTask.loadContext();
  } else {
    JSTask.context = null;
  }
  JSTask.loadInterventions().then(() => {
    JSTask.enrichedInterventions = JSTask.getEnrichedInterventionsObject();
  });
}

