actionConfiguration.body:
async function () {
  if (!JSTask.currentIntervention) {
    JSTask.payload = {
      interventionsInput: {
        plannedDate: moment(DatePickerAddIntervention.selectedDate).format("YYYY-MM-DD"),
        technicianId: AddInterventionTechnician.selectedOptionValue
      },
      ticketInput: {
        statut: "Assigned",
        plannerComment: PlannerComment.text
      }
    };
    JSTask.executeTask();
  } else {
    JSTask.currentIntervention.plannedDate = moment(DatePickerAddIntervention.selectedDate).format("YYYY-MM-DD");
    JSTask.currentIntervention.technicianId = AddInterventionTechnician.selectedOptionValue;
    modifyIntervention.run().then(response => {
      resetWidget("DatePickerAddIntervention");
      resetWidget("AddInterventionTechnician");
      JSTask.unselectIntervention();
      JSGeneral.init();
    }).catch(error => {
      showAlert("Une erreur est survenue : " + error.message, "error");
    });
  }
}

