actionConfiguration.body:
async function () {
  try {
    const response = await interventions.run({
      urlInterventions: JSTask.ticket.interventionsUrl
    });
    JSTask.interventions = response;
    if (JSTask.interventions.find(it => it.statut === "Planified")) {
      JSTask.currentIntervention = tasksList.data.find(it => it.caseId == JSTask.ticket.processInstanceId_string);
    }
  } catch (error) {
    showAlert("Error loading interventions: " + error.message, "error");
  }
}

