actionConfiguration.body:
async function (selectedTicket) {
  const index = JSTickets.tickets.findIndex(it => it.persistenceId === selectedTicket);
  if (JSTickets.tickets[index].userName === "") {
    userX.run({
      userId: JSTickets.tickets[index].userId
    }).then(() => {
      JSTickets.tickets[index].userName = userX.data.firstname + " " + userX.data.lastname;
      JSGeneral.dashBoardVisiblity = false;
      JSGeneral.TicketDetailsVisibility = true;
      resetWidget("InterventionList");
      JSTickets.currentTicket = selectedTicket;
      JSTask.initTask();
    });
  } else {
    JSGeneral.dashBoardVisiblity = false;
    JSGeneral.TicketDetailsVisibility = true;
    resetWidget("InterventionList");
    JSTickets.currentTicket = selectedTicket;
    JSTask.initTask();
  }
}

