actionConfiguration.body:
async function () {
  JSTickets.tickets = [];
  const [openedTicketsQuery, tasksListQuery] = await Promise.all([openedTickets.run(), tasksList.run()]);
  for (const it of openedTicketsQuery) {
    const task = tasksListQuery.find(taskVar => taskVar.caseId === it.processInstanceId_string) || null;
    let assignee = "";
    if (task && task.assigned_id) {
      assignee = JSTask.technicians.find(tech => tech.id.toString() === task.assigned_id)?.name || "";
    }
    const ticket = {
      persistenceId: it.persistenceId,
      persistenceId_string: it.persistenceId_string,
      userId: it.userId,
      userName: "",
      processInstanceId: it.processInstanceId,
      ticketSubject: it.ticketSubject,
      details: it.details,
      location: it.location,
      priority: it.priority,
      equipmentType: it.equipmentType.typeName,
      statut: it.statut,
      creationDate: it.creationDate,
      plannerComment: it.plannerComment,
      interventionsUrl: it.links.find(link => link.rel === "interventions").href.replace(/^\/API/, ""),
      task: task,
      assigneeId: task?.assigned_id,
      assignee: assignee
    };
    JSTickets.tickets.push(ticket);
  }
  if (filterText.text) {
    JSTickets.filteredTickets = JSTickets.filterTickets(filterText.text);
  } else {
    JSTickets.filteredTickets = JSTickets.tickets;
  }
}

