async function () {
  if (!appsmith.store.userData || !appsmith.store.groupData || !appsmith.store.userMemberships || !appsmith.store.technicians || !appsmith.store.managers || !appsmith.store.planners) {
    await user.run();
    await userInfo.run();
    await groups.run();
    JSIdentity.groups = groups.data;
    JSIdentity.groupPlannerId = JSIdentity.groups.find(it => it.name === "Planner")?.id.toString();
    JSIdentity.groupTechnicianId = JSIdentity.groups.find(it => it.name === "Technician")?.id.toString();
    JSIdentity.groupManagerId = JSIdentity.groups.find(it => it.name === "Manager")?.id.toString();
    await Promise.all([userMemberShips.run(), technicians.run(), managers.run(), planners.run()]);
    storeValue("userData", user.data);
    storeValue("userInfo", userInfo.data);
    storeValue("userMemberships", userMemberShips.data);
    storeValue("groupData", groups.data);
    storeValue("technicians", technicians.data);
    storeValue("managers", managers.data);
    storeValue("planners", planners.data);
    storeValue("loginPage", JSIdentity.loginPage);
  } else {
    console.log("Using cached data from store");
  }
}

