export default {
	context: null,
	task: {},
	ticket: {},
	initiator: {},
	photo: "",
	payload: {},
	confirmMessage: "",
	interventions: [],
	enrichedInterventions: [],
	currentIntervention: null,
	runningTask: false,
	technicians: appsmith.store.technicians || [],
	planners: appsmith.store.planners || [],
	managers: appsmith.store.managers || [],

	async loadInterventions() {
		try {
			const response = await interventions.run({ urlInterventions: this.ticket.interventionsUrl });
			this.interventions = response;
		} catch (error) {
			showAlert("Error loading interventions: " + error.message, "error");
		}
	},

	initTask() {
		this.ticket = JSTickets.tickets.find( it => it.persistenceId === JSTickets.currentTicket)
		if (!this.ticket) {
			JSTickets.unselectTicket();
			return;
		}
		this.task = this.ticket.task
		this.interventions = []
		this.photo = "";
		//this.loadInitiator();
		this.loadInterventions().then (() => {
			this.enrichedInterventions = this.getEnrichedInterventionsObject();
		});
	},
	updatePhoto (url) {
		this.photo = url;
	},
	getEnrichedInterventions () {
		const interventions = JSTask.interventions || [];
		return interventions.map((intervention) => {
			const tech = this.technicians.find(t => t.id == intervention.technicianId);
			const plan = this.planners.find(p => p.id == intervention.plannerId);
			return {
				...intervention,
				technicianName: tech ? tech.name : "Not assigned",
				plannerName: plan ? plan.name : "Unknow"
			};
		});
	},
	getEnrichedInterventionsObject () {
		return {
			interventions: JSTask.getEnrichedInterventions()
		};
	},

	async executeTasks() {
		for (const task of JSTickets.tasks) {
			await executeTask.run({ taskId: task.id });
		}
		closeModal(ModalTask.name);
	}
}

