export default {
	MyTicketsVisibility: myTicketsCount.data[0] > 0,
	TicketDetailsVisibility: false,
	ticketCounts: {},
	returnLabel: null,

	showTicketDetails () {
		this.MyTicketsVisibility = false;
		this.TicketDetailsVisibility = true;
	},
	hideTicketDetails () {
		this.MyTicketsVisibility = true;
		this.TicketDetailsVisibility = false;
	},

	colorStatut (str) {
		switch (str) {
			case "New":
				return "#be185d";
			case "Pending":
				return "#a16207";
			case "Assigned":
				return "#1e3a8a";
			case "Under Review": 
				return "#15803d";	
			case "Closed": 
				return "#15803d";
			default:
				return "#000000";
		}
	},

	formatCustomDate(dateString) {
		const dateObject = new Date(dateString);
		if (isNaN(dateObject.getTime())) {
			return "Date invalide";
		}
		const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
		const dayName = daysOfWeek[dateObject.getDay()];
		const year = dateObject.getFullYear();
		const month = dateObject.toLocaleString('en-US', { month: 'short' });
		const day = String(dateObject.getDate()).padStart(2, '0');
		const hasTime = dateString.includes('T');

		if (hasTime) {
			const hours = String(dateObject.getHours()).padStart(2, '0');
			const minutes = String(dateObject.getMinutes()).padStart(2, '0');
			const seconds = String(dateObject.getSeconds()).padStart(2, '0');
			return `${dayName} ${month} ${day} ${year} ${hours}:${minutes}`;
		} else {
			return `${dayName} ${month} ${day} ${year}`;
		}
	},

	async init() {
		await JSIdentity.userInfo();
		await myTicketsCount.run();
		await JSTickets.loadTickets();		
		this.hideTicketDetails();
		if (JSTickets.tasks.length > 0) {
			showModal(ModalTask.name);
		}
	},
}

