export default {
	context: null,
	task: {},
	ticket: {},
	initiator: {},
	photo: "",
	payload: {},
	confirmMessage: "",
	interventions: [],
	enrichedInterventions: [],
	currentIntervention: null,
	runningTask: false,
	technicians: appsmith.store.technicians || [],
	planners: appsmith.store.planners || [],
	managers: appsmith.store.managers || [],

	selectIntervention(index, statut) {
		this.currentIntervention = this.interventions[index];
		this.currentIntervention.statut = statut;
		DatePickerAddIntervention.setValue(this.currentIntervention.plannedDate);
		AddInterventionTechnician.setSelectedOption(this.currentIntervention.technicianId);
	},

	unselectIntervention() {
		this.currentIntervention = null
	},

	async loadContext() {
		try {
			const response = await taskContext.run();
			this.context = response;
		} catch (error) {
			showAlert("Error loading taskContext: " + error.message, "error");
		}
	},

	async loadInitiator() {
		try {
			const response = await userX.run({ userId: this.ticket.userId });
			this.initiator = response;
		} catch (error) {
			showAlert("Error loading ticketInitiator: " + error.message, "error");
		}
	},
	async loadInterventions() {
		try {
			const response = await interventions.run({ urlInterventions: this.ticket.interventionsUrl });
			this.interventions = response;
			if (this.interventions.find(it => it.statut === "Planified")) {
				this.currentIntervention = tasksList.data.find(it => it.caseId == this.ticket.processInstanceId_string)
			}
		} catch (error) {
			showAlert("Error loading interventions: " + error.message, "error");
		}
	},
	async executeTask () {
		this.runningTask = true;
		executeTask.run()
			.then((response) => {
			resetWidget("ResultIntervention");
			resetWidget("DateIntervention");
			resetWidget("CommentIntervention");
			JSTickets.loadTickets();
		})
			.catch((error) => {
			showAlert("Une erreur est survenue : " + error.message, "error");
		});
	},	

	initTask() {
		this.ticket = JSTickets.tickets.find( it => it.persistenceId === JSTickets.currentTicket)
		if (!this.ticket) {
			JSTickets.unselectTicket();
			return;
		}
		this.task = this.ticket.task
		this.interventions = []
		this.context = {};
		this.initiator = {};
		this.photo = "";
		this.payload = {};
		if (this.task) {
			this.loadContext();
		} else {
			this.context = null;
		}
		//this.loadInitiator();
		this.loadInterventions().then (() => {
			this.enrichedInterventions = this.getEnrichedInterventionsObject();
		});
