										 });
		} else {
			JSGeneral.dashBoardVisiblity = false;
			JSGeneral.TicketDetailsVisibility = true;
			resetWidget("InterventionList");
			this.currentTicket = selectedTicket;
			JSTask.initTask()
		}
	},

	unselectTicket() {
		JSGeneral.dashBoardVisiblity = true;
		JSGeneral.TicketDetailsVisibility = false;
		this.currentTicket = null;
	},

	async loadTickets() {
		this.tickets = [];
		const [openedTicketsQuery,tasksListQuery ] = await Promise.all([
			openedTickets.run(),
			tasksList.run()
		]);
		for (const it of openedTicketsQuery) {
			const task = tasksListQuery.find((taskVar) => taskVar.caseId === it.processInstanceId_string) || null;
			let assignee = "";
			if (task && task.assigned_id) {
				assignee = JSTask.technicians.find( tech => tech.id.toString() === task.assigned_id )?.name || "";
			}
			const ticket = {
				persistenceId: it.persistenceId,
				persistenceId_string: it.persistenceId_string,
				userId: it.userId,
				userName: "",
				processInstanceId: it.processInstanceId,
				ticketSubject: it.ticketSubject,
				details: it.details,
				location: it.location,
				priority: it.priority,
				equipmentType: it.equipmentType.typeName,
				statut: it.statut,
				creationDate: it.creationDate,
				plannerComment: it.plannerComment,
				interventionsUrl: it.links.find((link) => link.rel === "interventions").href.replace(/^\/API/, ""),
				task: task,
				assigneeId: task?.assigned_id,
				assignee: assignee
			};
			this.tickets.push(ticket);
		}
		if (filterText.text) {
			this.filteredTickets = this.filterTickets(filterText.text);
		} else {
			this.filteredTickets = this.tickets;
		}

	},

	filterTickets(str) {
		if (str.trim() === "" || !str) {
			this.filteredTickets = this.tickets;
		} else {
			const lowerStr = str.toLowerCase();
			this.filteredTickets = this.tickets.filter ((it) => 
																									it.userName.toLowerCase().includes(lowerStr) || 
																									it.ticketSubject.toLowerCase().includes(lowerStr) || 
																									it.location.toLowerCase().includes(lowerStr) || 
																									it.assignee.toLowerCase().includes(lowerStr) || 
																									it.equipmentType.toLowerCase().includes(lowerStr))
		}
	},

	findTicketByStatut(statut) {
		return (this.filteredTickets.filter( (it) => it.statut === statut))
	},

}

